import Nav from "./Nav";
import NotEnough from "./NotEnough";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

export default function Study({decks, setDecks}) {
  const params = useParams();
  const [deck, setDeck] = useState({});
  const [cards, setCards] = useState([]);

  /* for(let i of decks){
      if(i.id == params.deckId){
          setDeck(i)
          console.log(deck);
      }
  }
*/

useEffect(() => {
    async function loadDeck() {
      const response = await fetch(
        `http://localhost:5000/decks/${params.deckId}`
      );
      const deckFromAPI = await response.json();
      setDeck(deckFromAPI);
    }
    loadDeck();
  }, [params.deckId]);

  return (
    <div>
      <Nav deck={deck} />
      <h1>Study: {deck.name}</h1>
      <div class="card w-75">
        <div class="card-body">
          <h5 class="card-title">Card X of X</h5>
          <p class="card-text">Front of card</p>
          <button class="btn btn-secondary">Flip</button>
        </div>
      </div>
      <NotEnough deck={deck} />
    </div>
  );
}
