import React, { useState } from "react";

import { Link, useHistory, useParams } from "react-router-dom";
import {createDeck} from '../utils/api/index' 

export default function CreateDeck({ decks, setDecks }) {

  const initialState= {
    id:decks.length + 1,
    name:"",
    description:""
  }

  const [deck, setDeck] = useState({});
  let history = useHistory();
  const params = useParams();

  function handleChange({target}){
    setDeck({
      ...deck,
      [target.name]: target.value
    })
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    createDeck(deck);
    setDecks([...decks, deck])
    setDeck(initialState)
  }

//added on click to submit button changed it to link, dont know if it works
  return (
    <>
      <div>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/">Home</Link>
            </li>
            <li className="breadcrumb-item">Create Deck</li>{" "}
          </ol>
        </nav>
      </div>
      <div>
        <h1>Create Deck</h1>
        <form onSubmit={handleSubmit}>
          <div class="form-group">
            <label for="exampleFormControlInput1">Name</label>
            <input
              type="text"
              class="form-control"
              name="name"
              id="exampleFormControlInput1"
              placeholder="Deck Name"
              onChange={handleChange}
              value={deck.name}
            />
          </div>
          <div class="form-group">
            <label for="exampleFormControlTextarea1">Description</label>
            <textarea
              class="form-control"
              name="description"
              id="exampleFormControlTextarea1"
              placeholder="Brief Description of the Deck"
              rows="3"
              onChange={handleChange}
              value={deck.description}
            ></textarea>
          </div>
          <button
            type="cancel"
            class="btn btn-secondary"
            onClick={() => history.push("/")}
          >
            Cancel
          </button>
          <Link to={`/decks/${decks.length + 1}`}> <button className="btn btn-primary" type="submit">
              Sumbit
            </button></Link>
        </form>
      </div>
    </>
  );
}
