import { Link, useParams, useHistory } from "react-router-dom";
import React, { useState, useEffect } from "react";
import { readDeck, updateDeck} from "../utils/api/index";

export default function EditDeck({ decks, setDecks }) {
  const initializedState = {
    id: "",
    name: "",
    description: "",
    cards: [],
  };

  const [currentIndexDeck, setCurrentIndexDeck] = useState(0);
  const params = useParams();
  const [deck, setDeck] = useState(initializedState);
  const history = useHistory();

  useEffect(() => {
    async function loadDeck() {
      const deckFromAPI = await readDeck(params.deckId);
      setDeck(deckFromAPI);
      setCurrentIndexDeck(Object.values(params) - 1);
    }
    loadDeck();
  }, [params.deckId]);

  function handleChange({target}){
      setDeck({
          ...deck,
          [target.name]: target.value
      })
  }

  async function handleSubmit(event) {
    event.preventDefault();
    await updateDeck(deck);
    setDecks([...decks, deck])
    history.push(`/decks/${decks.length + 1}`)
  }

  return (
    <>
      <div>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/">Home</Link>
            </li>
            <li className="breadcrumb-item">{deck.name}</li>
            <li className="breadcrumb-item">Edit Deck</li>
          </ol>
        </nav>
      </div>

      <div>
        <h1>Edit Deck</h1>
        <form onSubmit={handleSubmit}>
          <div class="form-group">
            <label for="exampleFormControlInput1">Name</label>
            <input
              type="text"
              className="form-control"
              name="name"
              id="exampleFormControlInput1"
              placeholder="Deck Name"
              onChange={handleChange}
              value={deck.name}
            />
          </div>
          <div className="form-group">
            <label for="exampleFormControlTextarea1">Description</label>
            <textarea
              className="form-control"
              name="description"
              id="exampleFormControlTextarea1"
              placeholder="Brief Description of the Deck"
              rows="3"

              value={deck.description}
            ></textarea>
          </div>
          <button
            type="cancel"
            className="btn btn-secondary"
            onClick={() => history.push("/")}
          >
            Cancel
          </button>
          <button className="btn btn-primary" type="submit">
            Sumbit
          </button>
        </form>
      </div>
    </>
  );
}
