import React, {useState, useEffect} from 'react'


export default function NotEnough({deck}){

  const [cards, setCards] = useState({});

  useEffect(() => {
    async function loadCard() {
      const response = await fetch(
        `http://localhost:5000/decks/${deck.id}/cards`
      );
      const cardFromAPI = await response.json();
      setCards(cardFromAPI);
    }
    loadCard();
  }, [cards]);

  if(cards.length > 2){
      return null;
  }

return (
    <>
    <h1>Not enough cards.</h1>
    <p>You need at least 3 cards to study. There are only {cards.length} cards in this deck.</p>
    </>
)
}
