import {Link, useParams} from 'react-router-dom'
import React, {useState, useEffect} from 'react'
import { readDeck } from "../utils/api/index";


// started working on deck, but need to add delete button functionality 

export default function Deck({decks}) {

    const initializedState = {
      id: "",
      name: "",
      description: "",
      cards: [],
    };

    const params= useParams();
    const [deck, setDeck] = useState(initializedState);

    useEffect(() => {
        async function loadDeck() {
          const deckFromAPI = await readDeck(params.deckId);
          setDeck(deckFromAPI);
        }
        loadDeck();
      }, [params.deckId]);

  return (
<div>
    <div>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/">Home</Link>
          </li>
          <li className="breadcrumb-item">{deck.name}</li>
        </ol>
      </nav>
    </div>
    <h3>{deck.name}</h3>
    <p>{deck.description}</p>
    <Link to={`/decks/${deck.id}/edit`} class="btn btn-secondary">Edit</Link>
    <Link to={`/decks/${deck.id}/study`} class="btn btn-primary">Study</Link>
    <Link to={`/decks/${deck.id}/cards/new`} class="btn btn-primary">Add Cards</Link>
    <button type="delete" class="btn btn-danger">Delete</button>

</div>
  );
}
