import Nav from "./Nav";
import NotEnough from "./NotEnough";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import {readDeck} from '../utils/api/index'


export default function Study({decks}) {
  const params = useParams();
  const [deck, setDeck] = useState([]);
  const [cards, setCards] = useState([])
  const initializedState = {
      id:"",
      name:"",
      description:"",
      cards: [],
  }

useEffect(() => {
    setDeck(initialized)
    async function loadDeck() {
      const deckFromAPI = await readDeck(params.deckId)
      setDeck(deckFromAPI);
      console.log(deck.cards[0].front);
    }
    loadDeck();
  }, [params.deckId]);
  console.log(deck.cards);

if(deck.cards.length > 0){
  return (
    <div>
      <Nav deck={deck} />
      <h1>Study: {deck.name}</h1>
      <div class="card w-75">
        <div class="card-body">
          <h5 class="card-title">Card X of X</h5>
          <p class="card-text">{deck.cards[0].front}</p>
          <button class="btn btn-secondary">Flip</button>
        </div>
      </div>
      <NotEnough deck={deck} />
    </div>
  );
}
}
