import React from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import {Route, Switch} from 'react-router-dom';
import Home from './Home';
import CreateDeck from '../Content/CreateDeck';
import Study from '../Studying/Study'
import {useParams} from 'react-router-dom'
import {useState, useEffect} from 'react'
import Deck from '../Content/Deck'
import AddCard from '../Content/AddCard'
import EditCard from '../Content/EditCard'
import EditDeck from '../Content/EditDeck'


function Layout() {

  const params = useParams();
  const [deck, setDeck] = useState({});

  useEffect(() => {
    async function loadDeck() {
      const response = await fetch(
        `http://localhost:5000/decks/${params.deckId}`
      );
      const deckFromAPI = await response.json();
      setDeck(deckFromAPI);
    }
    loadDeck();
  }, [params.deckId]);



  return (
    <>
      <Header />
      <div className="container">
        <Switch>
        <Route exact path="/">
          <Home />
        </Route>
        {/* TODO: Implement the screen starting here */}
        <Route path='/decks/new'>
          <CreateDeck/>
        </Route>
        <Route path='/decks/:deckId/study'>
          <Study deck={deck}/>
        </Route> 
        <Route path="/decks/:deckId">
          <Deck/>
        </Route>  
        <Route path="/decks/:deckId/edit">
          <EditDeck/>
        </Route>
        <Route path="/decks/:deckId/cards/new">
          <AddCard/>
        </Route>  
        <Route path="/decks/:deckId/cards/:cardId/edit">
          <EditCard/>
        </Route>
        <Route path="">
        <NotFound />
        </Route>
        </Switch>
      </div>
    </>
  );
}

export default Layout;
