import Nav from "./Nav";
import NotEnough from "./NotEnough";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { readDeck } from "../utils/api/index";
import Card from './Card'


export default function Study({ decks }) {

    const initializedState = {
        id: "",
        name: "",
        description: "",
        cards: [],
      };

  const params = useParams();
  const [deck, setDeck] = useState(initializedState);
 // const [cards, setCards] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    async function loadDeck() {
      const deckFromAPI = await readDeck(params.deckId);
      setDeck(deckFromAPI);
    }
    loadDeck();
  }, [params.deckId]);
  console.log(deck.cards);


    return (
      <div>
        <Nav deck={deck} />
        <h1>Study: {deck.name}</h1>
        <div class="card w-75">
          <div class="card-body">
            <h5 class="card-title">Card X of X</h5>
            <p class="card-text">{cards[0].front}</p>
            <button class="btn btn-secondary">Flip</button>
          </div>
        </div>
        <NotEnough deck={deck} />
      </div>
    );

}
