import {Link, useParams} from 'react-router-dom'
import React, {useState, useEffect} from 'react'
import { readDeck } from "../utils/api/index";


// started working on deck, but need to add delete button functionality 

export default function Deck({decks}) {

    const initializedState = {
      id: "",
      name: "",
      description: "",
      cards: [],
    };

    const initializedStateCard = {
        id: "",
        front: "",
        back: "",
        deckId: "",
      };

    const params= useParams();
    const [deck, setDeck] = useState(initializedState);
    const [card, setCard] = useState(initializedStateCard)
    useEffect(() => {
        async function loadDeck() {
          const deckFromAPI = await readDeck(params.deckId);
          setDeck(deckFromAPI);
        }
        loadDeck();
      }, [params.deckId]);

      //edit link not working and neither is delete button

  return (
<div>
    <div>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/">Home</Link>
          </li>
          <li className="breadcrumb-item">{deck.name}</li>
        </ol>
      </nav>
    </div>
    <h3>{deck.name}</h3>
    <p>{deck.description}</p>
    <Link to={`/decks/${deck.id}/edit`} class="btn btn-secondary">Edit</Link>
    <Link to={`/decks/${deck.id}/study`} class="btn btn-primary">Study</Link>
    <Link to={`/decks/${deck.id}/cards/new`} class="btn btn-primary">Add Cards</Link>
    <button type="delete" class="btn btn-danger">Delete</button>
    <h2>Cards</h2>
    <div class="card w-75">
  <div class="card-body">
    <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
    <Link to={`/decks/${deck.id}/cards/${card.id}/edit`} class="btn btn-secondary">Edit</Link>
    <button type="delete" class="btn btn-danger">Delete</button>
  </div>
</div>
</div>
  );
}
