import { createDeck, readDeck, readCard } from "../utils/api";
import { useParams, Link, useHistory } from "react-router-dom";
import React, { useState, useEffect } from "react";

export default function EditCard() {
  const history = useHistory();
  const params = useParams();
  const [card, setCard] = useState();
  const [deck, setDeck] = useState();

  useEffect(() => {
    async function loadDeck() {
      const deckFromAPI = await readDeck(params.deckId);
      setDeck(deckFromAPI);
    }
    loadDeck();
  }, [params.deckId]);

  useEffect(() => {
    async function loadCard() {
      const cardFromAPI = await readCard(params.cardId);
      setCard(cardFromAPI);
    }
    loadCard();
  }, [params.cardId]);

  return (
    <div>
      <div>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/">Home</Link>
            </li>
            <li className="breadcrumb-item">Deck {deck.name}</li>
            <li className="breadcrumb-item">Edit Card {card.id}</li>
          </ol>
        </nav>
      </div>

      <div>
        <h1>{deck.name}: Add Card</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="front">Front</label>
            <textarea
              type="text"
              className="form-control"
              id="front"
              rows="3"
              name="front"
              placeholder="Front side of card"
              onChange={handleChange}
              value={card.front}
            ></textarea>
          </div>
          <div className="form-group">
            <label htmlFor="back">Back</label>
            <textarea
              className="form-control"
              id="back"
              placeholder="Back side of card"
              rows="3"
              name="back"
              onChange={handleChange}
              value={card.back}
            ></textarea>
          </div>
          <button
            type="done"
            className="btn btn-secondary"
            onClick={() => history.push(`/decks/${params.deckId}`)}
          >
            Done
          </button>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
