import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'

export default function NotEnough({deck}){

  const [cards, setCards] = useState({});

  useEffect(() => {
    async function loadCard() {
      const response = await fetch(
        `http://localhost:5000/decks/${deck.id}/cards`
      );
      const cardFromAPI = await response.json();
      setCards(cardFromAPI);
    }
    loadCard();
  }, [deck.id]);

  if(cards.length > 2){
      return null;
  }

return (
    <>
    <div>
    <h1>Not enough cards.</h1>
    <p>You need at least 3 cards to study. There are only {cards.length} cards in this deck.</p>
    </div>
    <div>
        <Link to="/decks/:deckId/cards/new"><button type="button" className="btn btn-primary">Add Cards</button></Link>
    </div>
    </>
)
}
