import { createDeck, readDeck, readCard} from "../utils/api";
import {useParams, Link, useHistory} from 'react-router-dom'

export default function EditCard() {

    const history = useHistory();
    const params = useParams();
    const [card, setCard] = useState();
    const [deck, setDeck] = useState();

    useEffect(() => {
        async function loadDeck() {
          const deckFromAPI = await readDeck(params.deckId);
          setDeck(deckFromAPI);
        };
        loadDeck();
      }, [params.deckId]);

      useEffect(() => {
        async function loadCard() {
          const cardFromAPI = await readCard(params.cardId);
          setCard(cardFromAPI);
        };
        loadCard();
      }, [params.cardId]);

  return (
    <div>
      <div>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/">Home</Link>
            </li>
            <li className="breadcrumb-item">Deck {deck.name}</li>
            <li className="breadcrumb-item">Edit Card {card.id}</li>
          </ol>
        </nav>
      </div>
    </div>
  );
}
