import { Link, useParams } from "react-router-dom";
import React, { useState, useEffect } from "react";
import { readDeck } from "../utils/api/index";

// started working on deck, but need to add delete button functionality

export default function Deck({ decks }) {
  const initializedState = {
    id: "",
    name: "",
    description: "",
    cards: [],
  };

  const initializedStateCard = {
    id: "",
    front: "",
    back: "",
    deckId: "",
  };

  const [currentIndexDeck, setCurrentIndexDeck] = useState(0);
  const params = useParams();
  const [deck, setDeck] = useState(initializedState);
  const [card, setCard] = useState(initializedStateCard);


  useEffect(() => {
    async function loadDeck() {
      const deckFromAPI = await readDeck(params.deckId);
      setDeck(deckFromAPI);
      setCard(deckFromAPI.cards);
      setCurrentIndexDeck(Object.values(params) - 1);
    }
    loadDeck();
  }, [params.deckId]);

  //edit link not working and neither is delete button
  //can't figure out how to map out cards
  return (
    <div>
      <div>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/">Home</Link>
            </li>
            <li className="breadcrumb-item">{deck.name}</li>
          </ol>
        </nav>
      </div>

      <div>
        <h3>{deck.name}</h3>
        <p>{deck.description}</p>
        <Link to={`/decks/${deck.id}/edit`} class="btn btn-secondary">
          Edit
        </Link>
        <Link to={`/decks/${deck.id}/study`} class="btn btn-primary">
          Study
        </Link>
        <Link to={`/decks/${deck.id}/cards/new`} class="btn btn-primary">
          Add Cards
        </Link>
        <button type="delete" class="btn btn-danger">
          Delete
        </button>
        <h2>Cards</h2>
        {console.log()}
        {deck.cards.map((deck, index) => (
          <div class="card">
            <div class="card-body">
              <p class="card-text">{deck[0]?.front}</p>
              <p class="card-text">{deck[0]?.back}</p>
              <Link
               to={`/decks/${deck.id}/cards/${card.id}/edit`}
                class="btn btn-secondary"
              >
                Edit
              </Link>
              <button type="delete" class="btn btn-danger">
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
